async function askQuestions() {
   
   let answers = await  inquirer
    .prompt([
      /* Pass your questions in here */
     {
        type:"list",
        name:"Operator",
        message:`Which Operation do you want to perform
        `,
        choices:["Addition","Subtraction","Multiplication","Division","Modulus","Exponent"]
     },
     {
        
        type:"number",
        name:"num1",
        message:"Enter number 1: ",
          
  },
    {
      
        type:"number",
        name:"num2",
        message:"Enter number 2: ",
        
  }
   
    ])
      if 
      (answers.Operator == "Addition") {
        console.log(chalk.cyan(`${answers.num1} + ${answers.num2} = ${answers.num1 + answers.num2}`))
      } else 
      if 
      (answers.Operator == "Subtraction") {
        console.log(chalk.cyan(`${answers.num1} - ${answers.num2} = ${answers.num1 - answers.num2}`))
      } else 
      if 
      (answers.Operator == "Multiplication") {
        console.log(chalk.cyan(`${answers.num1} * ${answers.num2} = ${answers.num1 * answers.num2}`))
      } else 
      if 
      (answers.Operator == "Division") {
        console.log(chalk.cyan(`${answers.num1} / ${answers.num2} = ${answers.num1 / answers.num2}`))
      } else 
      if (answers.Operator == "Modulus") {
        console.log(chalk.cyan(`${answers.num1} % ${answers.num2} = ${answers.num1 % answers.num2}`))
      } else 
      if (answers.Operator == "Exponent") {
        console.log(chalk.cyan(`${answers.num1} ** ${answers.num2} = ${answers.num1 ** answers.num2}`))
      } else ;

      

    
    
}
